INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (20, 'Самая эффективная схема отработки любого возражения', 'Присоединение, уточнение, аргументация, призыв к действию', 'Аргументация, призыв к действию', 'Вопрос, аргументация', 'Присоединение, уточнение, аргументация, призыв к действию');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (21, 'Перехват инициативы это ', 'Когда клиент готов отвечать на вопросы и понимает зачем ему это нужно', 'Когда клиент задаёт вопросы, а менеджер на них отвечает', 'Когда менеджер говорит, а клиент слушает', 'Когда клиент готов отвечать на вопросы и понимает зачем ему это нужно');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (22, 'Зачем менеджеру задавать вопросы клиенту? ', 'Чтобы не создавать пауз в разговоре ', 'Чтобы понять потребность, понять критерии выбора, квалифицировать клиента, определить какую услугу предложить ', 'Чтобы понять потребность ', 'Чтобы понять потребность, понять критерии выбора, квалифицировать клиента, определить какую услугу предложить ');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (23, 'Ваши действия при работе с возражениями клиентов:', 'Согласиться с частью возражения; прояснить ситуацию/задать уточняющие вопросы; аргументация возражения; призыв к действию ', 'Со всем соглашаться ; внимательно слушать; прояснить ситуацию/задать уточняющие вопросы; не спорить', 'Настаивать на своем/убеждать; призыв к действию; аргументация возражения; бросить трубку ', 'Согласиться с частью возражения; прояснить ситуацию/задать уточняющие вопросы; аргументация возражения; призыв к действию ');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (24, 'Назовите правильную структуру продающего сообщения: ', 'Приветствие, спец. предложение, выгода, дедлайн', 'Приветствие, спец. предложение, выгода, дедлайн, призыв и вопрос', 'Приветствие, дедлайн, спец. предложение, выгода, призыв и вопрос ', 'Приветствие, спец. предложение, выгода, дедлайн, призыв и вопрос');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (25, 'Цель звонка или сообщения по работе с бывшими клиентами? ', 'Проинформировать клиента о текущих акциях и предложить купить ', 'Получить отзыв ', 'Запустить диалог с клиентом, понять текущую ситуацию клиента', 'Запустить диалог с клиентом, понять текущую ситуацию клиента');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (26, 'Что такое пребукинг? ', 'Внести предоплату за товар/услугу ', 'Полностью оплатить товар/услугу ', 'Предварительно забронировать (без денег) товар/услугу', 'Предварительно забронировать (без денег) товар/услугу');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (27, 'Какое минимальное количество возражений должен отработать менеджер за один диалог с клиентом?', 'Одно, чтобы не быть навязчивым ', 'Минимум три ', 'Чем больше, тем лучше', 'Минимум три ');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (28, 'Назовите преимущества переписки с клиентами', 'На сообщения можно не отвечать во время диалога; Можно заранее подготовить текст; Могут переписываться разные сотрудники с одним клиентом', 'На сообщения можно не отвечать во время диалога; Могут переписываться разные сотрудники с одним клиентом; Параллельное общение с несколькими клиентами; Ответ может быть с задержкой до нескольких дней ', 'Можно прикрепить разные файлы; Можно заранее подготовить текст; Параллельное общение с несколькими клиентами; Могут переписываться разные сотрудники с одним клиентом', 'Можно прикрепить разные файлы; Можно заранее подготовить текст; Параллельное общение с несколькими клиентами; Могут переписываться разные сотрудники с одним клиентом');
INSERT INTO entrance_test_b2c (id, question, ans_1, ans_2, ans_3, correct_answer) VALUES (29, 'Что такое "дожим" по клиенту?', 'Любой ценой продать', 'Внести ясность в сделке', 'Вывести клиента на диалог', 'Внести ясночть в сделке');
